sp_widget.css:
.label {
  color: darkgray;
  font-size: 32px;
  font-weight: 500;
  text-align: center;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	var states_List = [];
	var statesList = new GlideRecord('x_95872_gocorona_states');
	statesList.query();
	while(statesList.next()){
		var stateInfo = {
			name : statesList.name.toString(),
			sys_id : statesList.sys_id.toString()
		}
			states_List.push(stateInfo);
	}
	data.states_List = states_List;

	var states = new GlideRecord('x_95872_gocorona_states');
	states.addQuery('name', 'Andhra Pradesh');
	states.query();
	if(states.next()){
		data.stateInfo = {
			name : states.name.toString(),
			active : states.active.toString(),
			confirmed : states.confirmed.toString(),
			deaths : states.death.toString(),
			recovered : states.recovered.toString()
		}
	}


})();
