sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var corona = new GoCorona();
    gs.info("Request: "+JSON.stringify(request.body.data));
    var writers = response.getStreamWriter(),
		hdrs = {};

	var request_details = request.body.data;
	var intent = request_details.queryResult.intent.displayName;

	var result = '';
	switch (intent){
		case "statewise":
			result = corona.getStateData(request_details.queryResult.parameters['case-type'], request_details.queryResult.parameters['indian-states'])+". Can I help you with anything else?";
			break;
		case "statewiseHistory":
			result = 0;
			break;
		default:
			result = "No intent found";
	}

	hdrs['Content-Type'] = 'application/json';
	response.setStatus(200);
	response.setHeaders(hdrs);

	var response_body = {
		"fulfillmentText": result,
		"payload" : {
			"google": {
				"expectUserResponse": true,
				"richResponse": {
					"items" : [
						{
							"simpleResponse": {
								"textToSpeech": result
							}
						}
					]
				}
			}
		}
	};
	gs.info("Response: "+JSON.stringify(response_body));
	writers.writeString(global.JSON.stringify(response_body));


})(request, response);
